"use strict";
// ------- Basic Data Type use in TypeScript
// Primitive Data type-------
// ------- String
let firstname = 'Madina';
// ------ Number
let roll = 20;
// ------ Boolean
let isAdmin = true;
// ------- Null
let NameField = null;
// ------ Undefined
let Myname = undefined;
// Non Primitive Data Type 
// -------- Array 
let Friends = ['madina', 'Mina', 'tara'];
Friends.push('madina');
let RollnumberList = [1, 2, 3];
RollnumberList.push(2);
// -------- Tuple 
// tuple --- array---order---type of values
let coordinator = [1, 2];
let mixedup = ['madina', 22];
let ageName = [22, 'madina', true];
